import React from "react";
import Card from "react-bootstrap/Card";

const BootstrapCards = ({ color, user }) => {
  const colors = {
    primary: "primary",
    secondary: "secondary",
    success: "success",
    danger: "danger",
    warning: "warning",
    info: "info",
    light: "light",
    dark: "dark"
  };

  let currentColor = colors[color];

  return (
    <div>
      <Card border={currentColor} style={{ width: "15rem" }}>
        <Card.Header style={{ backgroundColor: "lightgreen", color: "white" }}>
          {user.name}
        </Card.Header>
        <Card.Body>
          <Card.Title>{user.company.name}</Card.Title>
          <Card.Text>{user.company.catchPhrase}</Card.Text>
        </Card.Body>
      </Card>
      <br />
    </div>
  );
};

export default BootstrapCards;
